import { NextRequest, NextResponse } from 'next/server';
import clientPromise from '@/lib/mongodb';

export async function POST(req: NextRequest) {
  try {
    const client = await clientPromise;
    const db = client.db('shootingwala');
    
    // Sample categories
    const sampleCategories = [
      {
        name: "Wedding Photography",
        image: "data:image/svg+xml;base64,",
        selected: true,
        createdAt: new Date(),
      },
      {
        name: "Pre-Wedding Shoot",
        image: "data:image/svg+xml;base64,",
        selected: true,
        createdAt: new Date(),
      },
      {
        name: "Engagement Photography",
        image: "data:image/svg+xml;base64,",
        selected: true,
        createdAt: new Date(),
      },
      {
        name: "Birthday Photography",
        image: "data:image/svg+xml;base64,",
        selected: false,
        createdAt: new Date(),
      },
    ];

    // Sample cities
    const sampleCities = [
      {
        name: "Mumbai",
        image: "data:image/svg+xml;base64,",
        selected: true,
        createdAt: new Date(),
      },
      {
        name: "Delhi",
        image: "data:image/svg+xml;base64,",
        selected: true,
        createdAt: new Date(),
      },
      {
        name: "Bangalore",
        image: "data:image/svg+xml;base64,",
        selected: true,
        createdAt: new Date(),
      },
    ];

    const categories = db.collection('categories');
    const cities = db.collection('cities');

    // Clear existing data
    await categories.deleteMany({});
    await cities.deleteMany({});

    // Insert sample data
    const categoryResults = await categories.insertMany(sampleCategories);
    const cityResults = await cities.insertMany(sampleCities);

    return NextResponse.json({ 
      success: true, 
      categories: categoryResults.insertedIds,
      cities: cityResults.insertedIds
    });
  } catch (error: any) {
    console.error('Error adding sample data:', error);
    return NextResponse.json({ error: 'Failed to add sample data', details: error?.message || error?.toString() }, { status: 500 });
  }
} 